def send_confirm_mail(token: str, to_email: str, mailer: Mailer) -> None:
    html = get_confirm_mail_html(token=token)
    subject: str = "Завершите регистрацию в yourscript."

    mailer.send(to_email=to_email, html=html, subject=subject)
